import axios from "axios";
import { useState } from "react";
import App from "../App";

function TodoItem({ getAllTodos, todos }) {
  const [editText, setEditText] = useState(""); 
  const [editingId, setEditingId] = useState(null);

  async function deleteTodo(id) {
    try {
      await axios.delete(`http://localhost:8000/todos/${id}`);
    } catch (error) {
      console.log("Error deleting todo:", error);
    }
    getAllTodos();
  }

  async function updateTodo(id, newText) {
    try {
      await axios.put(`http://localhost:8000/todo/${id}`, {
        text: newText,
      });
      setEditText(""); 
      setEditingId(null); 
      getAllTodos();
    } catch (error) {
      console.error("Error updating todo:", error);
    }
  }

  async function toggleStatus(id, newStatus) {
    try {
      await axios.put(`http://localhost:8000/todo/${id}`, {
        status: newStatus,
      });
      getAllTodos();
    } catch (error) {
      console.error("Error toggling todo status:", error);
    }
  

   
    axios
      .put(`http://localhost:8000/todos/${id}`, updateTodo)
      .then(() => {
    
        getAllTodos();
      })
      .catch((error) => {
        console.log("Error updating todo status:", error);
      });
  }

  return (
    <div>
      {/* Map through the todos and render each one */}
      {todos.map((todo) => (
        <div key={todo._id}>
          <div>
            <input type="checkbox" onChange={() => toggleStatus(todo._id, !todo.status)} />
            <span>{todo.text}</span>
            <button onClick={() => deleteTodo(todo._id)}>
              <i className="material-icons">delete</i> 
            </button>
            <button
              onClick={() => {
                // Start editing by setting the editText and editingId state
                setEditText(todo.text);
                setEditingId(todo._id);
              }}
            >
              <i className="material-icons">edit</i> 
            </button>
          </div>
          
          {/* Render the text as editable input if currently being edited */}
          {editingId === todo._id && (
            <div>
              <input
                type="text"
                value={editText}
                onChange={(e) => setEditText(e.target.value)}
              />
              <button
                onClick={() => {
                  updateTodo(todo._id, editText);
                }}
              >
                Save
              </button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default TodoItem;
