function TodoItem({ getAllTodos, todos }) {
  function deleteTodo(id) {
    // Handle the delete logic here (e.g., make a request to delete the todo with the given id)

    // Update the user interface after you delete a task by calling getAllTodos
    getAllTodos();
  }

  function updateTodo(id) {
    const todoToUpdate = todos.find((todo) => todo._id === id);

    const updatedText = prompt(
      "Enter the updated text for the todo:",
      todoToUpdate.text
    );

    if (updatedText === null) {
      return;
    }

    const updatedTodos = todos.map((todo) => {
      if (todo._id === id) {
        return { ...todo, text: updatedText };
      } else {
        return todo;
      }
    });

    // Replace the existing `todos` array with the updated array
    setTodos(updatedTodos);
  }

  function toggleStatus(id) {
    const todoToToggle = todos.find((todo) => todo._id === id);

  // Create a new array of updated todos by mapping through the existing `todos` array
  const updatedTodos = todos.map((todo) => {
    if (todo._id === id) {
      // If the current todo has the matching id, toggle its status property
      return { ...todo, status: todo.status === "completed" ? "pending" : "completed" };
    } else {
      // Otherwise, return the todo as is
      return todo;
    }
  });

  // Replace the existing `todos` array with the updated array
  setTodos(updatedTodos);
}
  

  return (
    <div>
      {/* Map through the todos and render each one */}
      {todos.map((todo) => (
        <div key={todo._id}>
          <input type="checkbox" onChange={() => toggleStatus(todo._id)} />
          <span>{todo.text}</span>
          <button onClick={() => deleteTodo(todo._id)}>
            <i className="material-icons">delete</i>
          </button>
          <button onClick={() => updateTodo(todo._id)}>
            <i className="material-icons">edit</i>
          </button>
        </div>
      ))}
    </div>
  );
      }

export default TodoItem;
