import React, { useState } from "react";
import axios from "axios";

function TodoItem({ getAllTodos, todos }) {
  const [editText, setEditText] = useState(""); // State to manage the edited text
  const [editingId, setEditingId] = useState(null); // State to manage which item is being edited

  async function deleteTodo(id) {
    try {
      await axios.delete("http://localhost:8000/todo/${id}");
      getAllTodos();
    } catch (error) {
      console.error("Error deleting todo:", error);
    }
  }

  async function updateTodo(id, newText) {
    try {
      await axios.put("http://localhost:8000/todo/${id}", {
        text: newText,
      });
      setEditText(""); // Reset the editText state
      setEditingId(null); // Reset the editing state
      getAllTodos();
    } catch (error) {
      console.error("Error updating todo:", error);
    }
  }
async function toggleStatus(id, newStatus) {
    try {
      await axios.put("http://localhost:8000/todo/${id}", {
        status: newStatus,
      });
      getAllTodos();
    } catch (error) {
      console.error("Error toggling todo status:", error);
    }
  }

  return (
    <div>
      {/* Map through the todos and render each one /}
      {todos.map((todo) => (
        <div key={todo._id}>
          <div>
            <input type="checkbox" onChange={() => toggleStatus(todo._id, !todo.status)} />
            <span>{todo.text}</span>
            <button onClick={() => deleteTodo(todo._id)}>
              <i className="material-icons">delete</i> Delete
            </button>
            <button
              onClick={() => {
                // Start editing by setting the editText and editingId state
                setEditText(todo.text);
                setEditingId(todo._id);
              }}
            >
              <i className="material-icons">edit</i> Edit
            </button>
          </div>

          {/ Render the text as editable input if currently being edited */}
          {editingId === todo._id && (
            <div>
              <input
                type="text"
                value={editText}
                onChange={(e) => setEditText(e.target.value)}
              />
              <button
                onClick={() => {
                  // Save changes when the save button is clicked
                  updateTodo(todo._id, editText);
                }}
              >
                Save
              </button>
            </div>
          )}
        </div>
             )};

export default TodoItem;
