import TodoItem from "./TodoItem";

function TodoList({ todos, getAllTodos }) {
  return (
    <>
      <div>
        {/* Render the TodoItem component with todos and getAllTodos function */}
        <TodoItem todos={todos} getAllTodos={getAllTodos} />
      </div>
    </>
  );
}
function updateTodo ({ tasks, handleEdit }) {
  return (
    <div>
      {tasks.map((task) => (
        <TodoItem key={task.text} task={task} onEdit={handleEdit} />
      ))}
    </div>
  );
};

export default {TodoList, updateTodo};
