      await axios.post("http://localhost:8000/todos/create", newTas)
      .then((res) => { 
      setTodos(res.data.newTas)
      console.log(todos)})
    } catch (error) {
      console.log(error);
    };

    setTodos([...todos, newTas]);
    };


  return (
    <div className="App">
      <h1>Todo App</h1>
      <AddTodo getAllTodos={getAllTodos} />
      <TodoList todos={todos} getAllTodos={getAllTodos} />
    </div>
  );
}

export default App;
