import TodoList from "./TodoList";
import axios from "axios";
import { useState } from "react";
import App from "../App";

function TodoItem({ getAllTodos, todos }) {
  async function deleteTodo(id) {
    try {
      await axios.delete(`http://localhost:8000/todos/${id}`);
    } catch (error) {
      console.log("Error deleting todo:", error);
    }
    getAllTodos();
  }

  const updateTodo = ({ task, onEdit }) => {
    const handleEdit = () => {
      // Call the onEdit function passed as a prop
      onEdit(task.id);
    };

    return (
      <div>
        <h3>{task.name}</h3>
        <p>{task.description}</p>
        <button onClick={handleEdit}>Edit</button>
      </div>
    );
  };

  function toggleStatus(id) {
    // Find the todo item with the given id
    const todo = todos.find((todo) => todo._id === id);

    // Update the status property of the todo item
    const updatedTodo = { ...todo, status: !todo.status };

    // Make a request to update the todo item with the updated status
    axios
      .put(`http://localhost:8000/todos/${id}`, updateTodo)
      .then(() => {
        // Call getAllTodos to update the user interface
        getAllTodos();
      })
      .catch((error) => {
        console.log("Error updating todo status:", error);
      });
  }

  return (
    <div id="div1">
      {todos.map((todo) => (
        <div key={todo._id}>
          <input type="checkbox" onChange={() => toggleStatus(todo._id)} />
          <span>{todo.text}</span>
          <button onClick={() => deleteTodo(todo._id)}>
            <i className="material-icons">delete</i>
          </button>
          <button onClick={() => updateTodo(todo._id)}>
            <i className="material-icons">edit</i>
          </button>
        </div>
      ))}
    </div>
  );
}

export default TodoItem;
